<?php/***********************************************************    [WaiKuCms] (C)2011 - 2011 waikucms.com    	@function 标签管理    @Filename LabelAction.class.php $    @Author pengyong $    @Date 2011-11-17 15:08:21 $*************************************************************/class LabelAction extends CommonAction{	Public function _empty()	{ 		alert('方法不存在!',1);	}     public function index()    {		$label = M('label');		$list = $label->order('addtime desc')->select();		$this->assign('list',$list);		$this->display('index');    }	//添加标签	public function add()    {        $this->display('add');    }	//执行添加	public function doadd()    {		$label = M('label');		$label->create(); 		if($label->add())		{			alert('操作成功!',U('Label/index'));		}		alert('操作失败!',1);    }	public function edit()    {		$label = M('label');		$list = $label->where('id='.$_GET['id'])->find();		$this->assign('list',$list);        $this->display();    }		public function doedit()    {		$label = M('label');		$data['title'] = $_REQUEST['title'];		$data['content'] = $_REQUEST['content'];		$data['addtime'] = date('Y-m-d H:i:s');		if($label->where('id='.$_REQUEST['id'])->save($data))		{			alert('操作成功!',U('Label/index'));		}		alert('操作失败!',1);    }		public function del()    {		$type = M('label');		if($type->where('id='.$_GET['id'])->delete())		{			alert('操作成功!',U('Label/index'));		}		alert('操作失败!',1);    }}?>